package Mar28th;

public class Practice16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while (true) {
			System.out.println(i);
			if (i == 100) {
				break;
			}
			i++;
			
		}
		
		i = 0;
		do {
			System.out.println(i);
			i++;
		} while (i <= 0);

	}

}
